// Store representa un backend de deduplicaciÃ³n.
//   - Seen(hash)  â†’ true  si el hash ya existÃ­a.
//   - Mark(hash)  â†’ persiste el hash (cuando es nuevo).
//   - Close()     â†’ libera recursos (flush, close, etc.).
type Store interface {
	Seen(hash string) bool
	Mark(hash string) error
	Close() error
}

// HashTiddler genera un SHA-256 estable a partir de campos que
// identifican de manera Ãºnica la versiÃ³n de un tiddler.
// Cambios en Title, Modified o Text â‡’ nuevo hash.
func HashTiddler(t models.Tiddler) string {
	h := sha256.New()
	h.Write([]byte(t.Title))
	h.Write([]byte{0})
	h.Write([]byte(t.Modified))
	h.Write([]byte{0})
	h.Write([]byte(t.Text))
	return hex.EncodeToString(h.Sum(nil))
